(function () {
    // Whole-script strict mode syntax
    'use strict';
    // Define cepan configurations
    angular.module('app').config(['$locationProvider', '$httpProvider', config]).run([run]);
    // Config pseudo constructor function
    function config($locationProvider, $httpProvider) {
        // Activate HTML5 mode navigation
        $locationProvider.html5Mode({
          enabled: true,
          requireBase: false
        });
        // Toastr messages configuration
        // For more options visit: http://codeseven.github.io/toastr/demo.html
        toastr.options = {
            "closeButton": true,
            "debug": false,
            "newestOnTop": false,
            "progressBar": false,
            "positionClass": "toast-bottom-right",
            "preventDuplicates": false,
            "onclick": null,
            "showDuration": "300",
            "hideDuration": "1000",
            "timeOut": "5000",
            "extendedTimeOut": "1000",
            "showEasing": "swing",
            "hideEasing": "linear",
            "showMethod": "fadeIn",
            "hideMethod": "fadeOut"
        };
        // Register our authentication token interceptor
        // $httpProvider.interceptors.push('AuthInterceptorFactory');
    }
    // Run pseudo constructor function
    function run() {
        // Show welcome toastr message
        toastr.success('Hello admin!');
    }
}());
(function() {
  'use strict';

    // Whole-script strict mode syntax
 
    // Define settings variable
  var settings = {
      //baseAPIUrl: 'https://cepan-admin.herokuapp.com/',
      baseAPIUrl: 'http://localhost:3000/',
      authTokenKey: 'token',
      endpoints: {
          signIn: 'user/signin',
          signUp: 'user/signup'
      }
  };
    // Define settings constant
  angular.module('app').constant('settings', settings);
}());
(function () {
    // Whole-script strict mode syntax
    'use strict';
    // Define home controller with its dependencies
    angular.module('app').controller('HomeController', ['$rootScope', homeController]);
    // Home controller pseudo constructor function
    function homeController($rootScope) {
        // Make a reference to keyword this into a more meaningful variable such as vm standing for view model
        var vm = this;
        /**
         * Native and bindable properties
         */
        // Main page title
        $rootScope.title = 'hello';
        /**
         * Internal functions
         */
        // Initialize home controller
        function _init() {
            // Show welcome message
            toastr.info('Home Page');
        }

        // Call _init function at the very beginning to initialize home controller
        _init();
    }
}());