// app.module.js
//wrap the components in an immediately invoked function 
//IIFE removes variables from the global scope. This helps prevent variables and function declarations from living longer than expected in the global scope, which also helps avoid variable collisions.
(function() {
  'use strict';
  //define the app.module.js
  angular
    .module('app', [
      'ui.router', 
      'ui.bootstrap'
    ]);
}())



(function () {
    // Whole-script strict mode syntax
    'use strict';
    // Define cepan routes
    angular.module('app').config(['$stateProvider', '$urlRouterProvider', config]);
    // Config pseudo constructor function
    function config($stateProvider, $urlRouterProvider) {
        // For any unmatched url, redirect to /state1
        $urlRouterProvider.otherwise("/start");
        // Now set up the states
        $stateProvider
            .state('home', {
                url: '/start',
                templateUrl: 'partials/layout/home.html',
                controller: 'HomeController as vm'
            })
            .state('signin', {
                url: '/ingresar',
                templateUrl: 'partials/user/signin.html',
                controller: 'SignInController as vm'
            })
            .state('signout', {
                url: '/salir',
                controller: 'LogOutController as vm'
            })
            .state('signup', {
                url: '/registrar',
                templateUrl: 'partials/user/signup.html',
                controller: 'SignUpController as vm'
            })
    }
}());
(function () {
    // Whole-script strict mode syntax
    'use strict';
    // Define cepan configurations
    angular.module('app').config(['$locationProvider', '$httpProvider', config]).run([run]);
    // Config pseudo constructor function
    function config($locationProvider, $httpProvider) {
        // Activate HTML5 mode navigation
         $locationProvider.html5Mode(true);
        // Toastr messages configuration
        // For more options visit: http://codeseven.github.io/toastr/demo.html
        toastr.options = {
            "closeButton": true,
            "debug": false,
            "newestOnTop": false,
            "progressBar": false,
            "positionClass": "toast-bottom-right",
            "preventDuplicates": false,
            "onclick": null,
            "showDuration": "300",
            "hideDuration": "1000",
            "timeOut": "5000",
            "extendedTimeOut": "1000",
            "showEasing": "swing",
            "hideEasing": "linear",
            "showMethod": "fadeIn",
            "hideMethod": "fadeOut"
        };
        // Register our authentication token interceptor
        $httpProvider.interceptors.push('AuthInterceptorFactory');
    }
    // Run pseudo constructor function
    function run() {
        // Show welcome toastr message
        toastr.success('Hello admin!');
    }
}());
(function() {
  'use strict';

    // Whole-script strict mode syntax
 
    // Define settings variable
  var settings = {
      //baseAPIUrl: 'https://cepan-admin.herokuapp.com/',
      baseAPIUrl: 'http://localhost:3000/',
      authTokenKey: 'token',
      endpoints: {
          signIn: 'user/signin',
          signUp: 'user/signup'
      }
  };
    // Define settings constant
  angular.module('app').constant('settings', settings);
}());